            profil : "",
            nb_mess : 0,
            // liste : [
            //     {changePage:this.props.changePage, isUtilisateur:false, delMess:this.delMess, idMessage:4, pseudo:"elMachoProtector", date:"27/02/2022", message: "Update de la dernière fois : on m'a parlé de Encanto: la fantastique famille Madrigal. Je déconseille !! Le film est pas mal mais les chansons sont encore plus entétantes que celle de la Reine des Neiges, dont je voulais me débarasser :'("},
            //     {changePage:this.props.changePage, isUtilisateur:false, delMess:this.delMess, idMessage:3, pseudo:"elMachoProtector", date:"17/02/2022", message: "Quelqu'un aurait des films pour enfants à conseiller ?? J'en peux plus de revisionner la reine des neiges avec mes gosses !! Aidez-moi XDDD"},
            //     {changePage:this.props.changePage, isUtilisateur:false, delMess:this.delMess, idMessage:2, pseudo:"darkSasukeVegeta", date:"07/02/2022", message: "NAN MAI WSSSHHH, ON SE RAIVOOOOOLTE ISSIIIIIII !!!! JPP LES GENS QUI RESPECTENT PAS LES PIGEONS VOUS ETES DES GROS MECHANT, WSH ILS ON UN COEUR AUSSI VOUS RESPECTER R"},
            //     {changePage:this.props.changePage, isUtilisateur:true, delMess:this.delMess, idMessage:1, pseudo:"filanteetoile", date:"01/01/2022", message: "Bonne annee !!!! Pleins de bonnes choses à tous, des bisous et plein de bonne humeur !!!"},
            //     {changePage:this.props.changePage, isUtilisateur:true, delMess:this.delMess, idMessage:0, pseudo:"filanteetoile", date:"01/01/2022", message: "Bonne annee !!!! Pleins de bonnes choses à tous, des bisous et plein de bonne humeur !!!"}
            // ]
            listeMess : []
        }
    }

    async recupereLogin() {
        console.log("appel de recupereLogin()")
        let t = this.context;
        let id = t.id;
        await axios.get(`http://localhost:4000/api/user/${id}`)
        .then((res) => {
            this.setState({
                profil : res.data['user']['login'],
                nb_mess : this.state.nb_mess,
                listeMess : this.state.listeMess
            });
            console.log("recupereLogin() : profil=" + this.state.profil);
        })
        .catch(e => {console.log("erreur getLogin ListeMessages")})
    }

    async recupereNbMess() {
        console.log("appel de recupereNbMess()")
        await axios.get("http://localhost:4000/apimessages/infos")
            .then((res) => {
                this.setState({
                    profil: this.state.profil,
                    nb_mess : res.data['count'],
                    listeMess : this.state.listeMess
                });
                console.log("recupereNbMess() : nb_mess=" + this.state.nb_mess);
            })
            .catch(e => {console.log("erreur getNbMess ListeMessages")})
    }

    // async recupereMessages() {
    //     console.log("appel de recupereMess()")
    //     for (let i=0; i<this.nb_mess; i++) {
    //         console.log("boucle " + i)
    //         let id_mess = this.state.liste_idMessages[i];

    //         await axios.get(`http://localhost:4000/apimessages/message/${id_mess}`)
    //             .then((res) => {
    //                 this.state.listeMess.unshift(res.data)
    //                 this.setState({
    //                     profil: this.state.profil,
    //                     nb_mess : this.state.nb_mess,
    //                     liste_idMessages : this.state.liste_idMessages,
    //                     listeMess: this.state.listeMess
    //                 })
    //             })
    //             .catch(e => {console.log("erreur getMessages ListeMessages")})
    //     }
    // }

    async recupereMessages(nb) {
        console.log("appel de recupereMess()");
        await axios.get("http://localhost:4000/apimessages/message", {nbAffichage : nb})
            .then((res) => {
                this.setState({
                    profil : this.state.profil,
                    nb_mess : this.state.nb_mess,
                    listeMess : res.data
                })
            })

    }

    // ajoutMessage(msg) {
    //     console.log("ajoutMessage(msg) de ListeMessages : msg=" + msg);
    //     const d = new Date();
    //     this.state.liste.unshift({ 
    //         changePage:this.props.changePage, 
    //         isUtilisateur:true, 
    //         delMess:this.delMess, 
    //         idMessage:0,
    //         pseudo:this.state.profil,
    //         date:d.toLocaleDateString(),
    //         message:msg
    //     });
    //     this.setState({
    //         profil : this.state.profil,
    //         liste: this.state.liste
    //     });
    // }

    async ajoutMessageV2(msg) {
        await axios.put(`http://localhost:4000/apimessages/message`, {
                message: msg,
                date: new Date().toLocaleDateString(),
                idUser: this.state.profil
            }
        )
        .then((res) => {
            console.log("dans le then du put de ajoutMessageV2");
            // this.state.listeMess.unshift({
            //     pseudo:this.state.profil,
            //     date:new Date().toLocaleDateString(),
            //     message:msg,
            //     _id:res.data['id']
            // })
            this.setState({
                profil: this.state.profil,
                nb_mess: this.state.nb_mess + 1,
                listeMess: this.state.listeMess
            });
            console.log("on a appelé setState : nbMess=" +this.state.nb_mess);
        })
        .catch(e => {console.log({error:e.response.data.message})})

        // await this.recupereMessages(10);
    }

    async ajoutmessage(msg) {
        await this.ajoutMessageV2(msg)
            .then(() => this.recupereMessages(10))
    }

    // delMess(indexMess) {
    //     for (var i=0; i<this.state.liste.length; i++) {
    //         if (this.state.liste[i].idMessage === indexMess) {
    //             this.state.liste.splice(i,1);
    //         }
    //     }
    //     this.setState({
    //         profil : this.state.profil,
    //         nb_mess : this.state.nb_mess,
    //         listliste_idMessagese: this.state.liste_idMessages,
    //         listeMess: this.state.listeMess
    //     })
    // }

    // componentDidUpdate() {
    //     this.recupereMessages(10);
    // }

    render() {
        if (this.state.profil === "") this.recupereLogin();
        if (this.state.nb_mess === 0) this.recupereNbMess();
        if (this.state.listeMess.length === 0) this.recupereMessages(10);

        return(
            <div className="messages">
                <NvMessage ajoutMessage={this.ajoutMessage} recupereMessages={this.recupereMessages}/>
                <h3>Liste de Messages</h3>
                <ul className="liste_messages">
                    {this.state.listeMess.map((mess, index) => (
                        <div key={index}>
                            <li>
                                <Message 
                                    changePage={this.props.changePage} 
                                    isUtilisateur={(mess.idUser!==this.state.profil)? false : true} 
                                    // delMess={mess.delMess}
                                    idMessage={mess._id}
                                    pseudo={mess.idUser}
                                    date={mess.date}
                                    message={mess.message}
                                />
                            </li>
                        </div>
                    ))}
                </ul>
            </div>
        )
    }
}
ListeMessages.contextType = IdContext;

export default ListeMessages
