import React, {Component} from "react";
import Message from './Message'
import NvMessage from './NvMessage'
import { IdContext } from "../context/id_context";
import axios from "axios";

class ListeMessages extends Component{
    constructor(props){
        super(props)
        this.ajoutMessage = this.ajoutMessage.bind(this)
        this.delMess = this.delMess.bind(this)
        this.state = {
            profil : "",
            liste : [
                {changePage:this.props.changePage, isUtilisateur:false, delMess:this.delMess, idMessage:4, pseudo:"elMachoProtector", date:"27/02/2022", message: "Update de la dernière fois : on m'a parlé de Encanto: la fantastique famille Madrigal. Je déconseille !! Le film est pas mal mais les chansons sont encore plus entétantes que celle de la Reine des Neiges, dont je voulais me débarasser :'("},
                {changePage:this.props.changePage, isUtilisateur:false, delMess:this.delMess, idMessage:3, pseudo:"elMachoProtector", date:"17/02/2022", message: "Quelqu'un aurait des films pour enfants à conseiller ?? J'en peux plus de revisionner la reine des neiges avec mes gosses !! Aidez-moi XDDD"},
                {changePage:this.props.changePage, isUtilisateur:false, delMess:this.delMess, idMessage:2, pseudo:"darkSasukeVegeta", date:"07/02/2022", message: "NAN MAI WSSSHHH, ON SE RAIVOOOOOLTE ISSIIIIIII !!!! JPP LES GENS QUI RESPECTENT PAS LES PIGEONS VOUS ETES DES GROS MECHANT, WSH ILS ON UN COEUR AUSSI VOUS RESPECTER R"},
                {changePage:this.props.changePage, isUtilisateur:true, delMess:this.delMess, idMessage:1, pseudo:"filanteetoile", date:"01/01/2022", message: "Bonne annee !!!! Pleins de bonnes choses à tous, des bisous et plein de bonne humeur !!!"},
                {changePage:this.props.changePage, isUtilisateur:true, delMess:this.delMess, idMessage:0, pseudo:"filanteetoile", date:"01/01/2022", message: "Bonne annee !!!! Pleins de bonnes choses à tous, des bisous et plein de bonne humeur !!!"}
            ]
        }
    }

    async recupereLogin() {
        let t = this.context;
        let id = t.id;
        console.log("t.id ")
        await axios.get(`http://localhost:4000/api/user/${id}`)
        .then((res) => {
            this.setState({
                profil : res.data['user']['login'],
                liste : this.state.liste
            });
        })
        .catch(e => {console.log("erreur getLogin ListeMessages")})
    }

    ajoutMessage(msg) {
        const d = new Date();
        this.state.liste.unshift({ 
            changePage:this.props.changePage, 
            isUtilisateur:true, 
            delMess:this.delMess, 
            idMessage:0, 
            pseudo:this.state.profil,
            date:d.toLocaleDateString(),
            message:msg
        });
        this.setState({
            profil : this.state.profil,
            liste: this.state.liste
        });
    }

    // async ajoutMessageV2(msg) {
    //     await axios.put("http://localhost:4000/apimessages/user/:user_id(\\d+)/message", {
    //             idMessage: document.getElementById("login").value,
    //             message: msg,
    //             date: new Date().toLocaleDateString(),
    //             idUser: axios.get()
    //         }
    //     )
    //     .then(() => {
    //         this.props.getConnected()})
    //     .catch(e => {console.log({error:e.response.data.message})})
    // }

    delMess(indexMess) {
        for (var i=0; i<this.state.liste.length; i++) {
            if (this.state.liste[i].idMessage === indexMess) {
                this.state.liste.splice(i,1);
            }
        }
        this.setState({
            profil : this.state.profil,
            liste: this.state.liste
        })
    }

    render() {
        return(
            <div className="messages">
                <NvMessage ajoutMessage={this.ajoutMessage} />
                <h3>Liste de Messages</h3>
                <ul className="liste_messages">
                    {this.state.liste.map((mess, index) => (
                        <div key={index}>
                            <li>
                                <Message 
                                    changePage={mess.changePage} 
                                    isUtilisateur={mess.isUtilisateur} 
                                    delMess={mess.delMess}
                                    idMessage={mess.idMessage}
                                    pseudo={mess.pseudo}
                                    date={mess.date}
                                    message={mess.message}
                                />
                            </li>
                        </div>
                    ))}
                </ul>
            </div>
        )
    }
}
ListeMessages.contextType = IdContext;

export default ListeMessages
