import React, {Component} from "react";
import Message from './Message'
import NvMessage from './NvMessage'

class ListeMessages extends Component{
    constructor(props){
        super(props)
        this.ajoutMessage = this.ajoutMessage.bind(this)
        this.delComm = this.delComm.bind(this)
        this.listeActual = this.listeActual.bind(this)
        this.state = {
            liste: [
            <Message 
                changePage={this.props.changePage} 
                delComm={this.delComm} 
                isNuit={this.props.isNuit}
                idMessage={4} pseudo="elMachoProtector" 
                date="27/02/2022"
                message="Update de la derniÃ¨re fois : on m'a parlÃ© de Encanto: la fantastique famille Madrigal. Je dÃ©conseille !! Le film est pas mal mais les chansons sont encore plus entÃ©tantes que celle de la Reine des Neiges, dont je voulais me dÃ©barasser :'(" />

                , <Message 
                    changePage={this.props.changePage} 
                    delComm={this.delComm} 
                    nuit={this.props.nuit} 
                    isNuit={this.props.isNuit}
                    idMessage={3}pseudo="elMachoProtector" date="17/02/2022"
                    message="Quelqu'un aurait des films pour enfants Ã  conseiller ?? J'en peux plus de revisionner la reine des neiges avec mes gosses !! Aidez-moi XDDD" />

                , <Message 
                    changePage={this.props.changePage} 
                    delComm={this.delComm}
                    isNuit={this.props.isNuit}
                    idMessage={2} 
                    pseudo="darkSasukeVegeta" date="07/02/2022"
                    message="NAN MAI WSSSHHH, ON SE RAIVOOOOOLTE ISSIIIIIII !!!! JPP LES GENS QUI RESPECTENT PAS LES PIGEONS VOUS ETES DES GROS MECHANT, WSH ILS ON UN COEUR AUSSI VOUS RESPECTER R" />

                , <Message 
                    changePage={this.props.changePage} 
                    isUtilisateur = {true} 
                    delComm={this.delComm} 
                    isNuit={this.props.isNuit}
                    idMessage={1} 
                    pseudo="filanteetoile" 
                    date="01/01/2022"
                    message="Bonne annee !!!! Pleins de bonnes choses Ã  tous, des bisous et plein de bonne humeur !!!" />

                , <Message 
                    changePage={this.props.changePage} 
                    isUtilisateur = {true} 
                    delComm={this.delComm} 
                    isNuit={this.props.isNuit} 
                    idMessage={0} 
                    pseudo="filanteetoile" 
                    date="01/01/2022"
                    message="Bonne annee !!!! Pleins de bonnes choses Ã  tous, des bisous et plein de bonne humeur !!!" />
            ]
        }
    }

    ajoutMessage(msg) {
        console.log("")
        console.log("j'ajoute un message")
        this.state.liste.unshift(
        <Message 
        changePage={this.props.changePage} 
        delComm={this.delComm} 
        isNuit={this.props.isNuit} 
        isUtilisateur = {true} 
        idMessage={Math.floor(Math.random() * 1000) + 8}
        pseudo="filanteetoile" 
        date="17/02/2022"
        message={msg} />
        );
        this.setState({
            liste: this.state.liste
        })
    }

    delComm(indexMess) {
        for (var i=0; i<this.state.liste.length; i++) {
            if (this.state.liste[i].props.idMessage == indexMess) {
                this.state.liste.splice(i,1);
            }
        }
        this.setState({
            liste: this.state.liste
        })
    }

    listeActual() {
        this.setState({
            liste : this.state.liste
        })
        console.log("j'ai mis Ã  jour la liste")
        return (this.state.liste)
    }

    render() {
        var l = this.props.isNuit() ? this.listeActual() : this.state.liste
        if (this.props.isNuit()) {
            console.log("il fait nuit")
            this.setState({})
        } else {
            console.log("il fait jour")
        }
        return(
            <div className="messages">
                <NvMessage ajoutMessage={this.ajoutMessage} />
                <h3>Liste de Messages</h3>
                <div className="liste_messages">
                    {l}
                </div>
            </div>
        )
    }
}

export default ListeMessages
