import React, { Component } from 'react'
import UtilisateurMessage from './UtilisateurMessage.js'
import Header from '../header/Header'
import ListeAmis from './ListeAmis'
import './utilisateur.css'
import {ContextCombinedThemeAndId} from "../context/combined_context"
import ProvideCombinedContext from '../context/ProvideCombinedContext';
import axios from 'axios'


class Utilisateur extends Component {
    constructor(props) {
        super(props);
        this.recupereLogin = this.recupereLogin.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            profil : this.props.profilVisited,
            surProfil : ""
        }
    }

    async recupereLogin() {
        console.log("appel de recuperLogin()");
        let t = this.context;
        let id = t.contextId.id;
        await axios.get(`http://localhost:4000/api/user/${id}`)
        .then((res) => {
            if (this.state.profil === "") {
                this.setState({
                    profil : res.data['user']['login'],
                    surProfil : res.data['user']['login']
                });
            } else {
                this.setState({
                    profil : this.props.profilVisited,
                    surProfil : res.data['user']['login']
                });
            }
            
        })
        .catch(e => {console.log("erreur get Utilisateur")})
    }
    
    handleClick() {
        this.setState()
    }

    render() {
        let t = this.context;
        let n = (t.contextTheme.theme === 'light') ? "" : "V2"
        if (this.state.surProfil === "") this.recupereLogin();

        console.log("render Utilisateur : profil=" + this.state.profil);
        console.log("render Utilisateur : surProfil=" + this.state.surProfil);

        return (
            <div>
                <Header changePage={this.props.changePage} setLogout={this.props.setLogout} isUtilisateur={this.state.profil === this.state.surProfil} handleClick={this.handleClick}/>
                <div className={"corps"+n}>
                    <aside className="liste_contacts">
                        <h3> Liste des contacts </h3>
                        <ListeAmis id={t.contextId.id}/>
                    </aside>
                    <div className="messages">
                        <div className={"titre_page"+n}>
                            <i className="fa-solid fa-user fa-5x"></i>
                            <div>
                                <h1>{"Profil de " + this.state.profil}</h1>
                                {(this.state.profil !== this.state.surProfil) && 
                                    <button id="ajout_contact">Ajouter en contact</button>
                                }
                            </div>
                         </div>
                        <UtilisateurMessage profil={this.state.profil} isUtilisateur={this.state.profil === this.state.surProfil}/>
                    </div>
                </div>
            </div>
        );
    }
}
Utilisateur.contextType = ContextCombinedThemeAndId;

const WrappedUtilisateur = props => {
    return (
      <ProvideCombinedContext>
        <Utilisateur profilVisited={props.profilVisited} changePage={props.changePage} setLogout={props.setLogout} isUtilisateur={true}/>
      </ProvideCombinedContext>
    );
};
export default WrappedUtilisateur;
