import React, { Component } from 'react';
import NavigationPanel from './NavigationPanel'
import './messages/message.css'
import {IdContext} from "./context/id_context"

class MainPage extends Component {
    constructor(props) {
        super(props);
        this.getConnected = (idU) => {
            console.log("getConnected de MainPage");
            this.setState(state => ({
                isConnected : true,
                page: 'pageAccueil', 
                idUser: idU
            }))
            console.log("id : " + this.state.idUser);
            console.log("isConnected : " + this.state.isConnected);
        }
        this.state = {
            page : 'connexion',
            isConnected : false,
            theme : {idUser : null,
                    getConnected : this.getConnected}
            // idUser : null,
            // getConnected : this.getConnected
        }
        this.changePage = this.changePage.bind(this);
        this.setLogout = this.setLogout.bind(this);
    }

    setLogout() {
        this.setState({ isConnected: false, page: 'connexion', idUser:null });
    }

    changePage(etat) {
        this.setState({isConnected : this.state.isConnected, page : etat, idUser:this.state.iduser});
    }

    render() {
        return (
            <IdContext.Provider value={this.state.idUser}>
                <NavigationPanel setLogout={this.setLogout} changePage={this.changePage} isConnected={this.state.isConnected} page={this.state.page} />
            </IdContext.Provider>
        )
    }
}

export default MainPage;
