import React, { Component } from 'react';
import NavigationPanel from './NavigationPanel'
import './messages/message.css'
import {IdContext} from "./context/id_context"

class MainPage extends Component {
    constructor(props) {
        super(props);
        this.getConnected = (idU) => {
            console.log("getConnected de MainPage");
            this.setState(state => ({
                isConnected : true,
                page: 'pageAccueil', 
                idUser: idU
            }))
            
        }
        this.state = {
            page : 'connexion',
            isConnected : false,
            idUser : null

        }
        this.changePage = this.changePage.bind(this);
        this.getConnected = this.getConnected.bind(this);
        this.setLogout = this.setLogout.bind(this);
    }

    // getConnected(idU){
    //     this.setState({isConnected : true, page : 'pageAccueil', idUser:idU});
    // }

    setLogout() {
        this.setState({ isConnected: false, page: 'connexion', idUser:null });
    }

    changePage(etat) {
        this.setState({isConnected : this.state.isConnected, page : etat, idUser:this.state.iduser});
    }

    render() {
        return (
            <NavigationPanel getConnected={this.getConnected} setLogout={this.setLogout} changePage={this.changePage} isConnected={this.state.isConnected} page={this.state.page} />
        )
    }
}

export default MainPage;
