import React, { Component } from 'react'

class BoutonsComm extends Component {
    constructor(props) {
        super(props)
        this.liker = this.liker.bind(this)
        this.state = {
            like: false
        }
    }

    liker() {
        this.setState({ like: !this.state.like })
    }

    render() {
        if (this.state.like) {
            return (
                <div className="boutonsComm">
                    <i className="fa-solid fa-heart" onClick={this.liker}></i>
                    <i className="fa-regular fa-comment"></i>
                    <i className="fa-solid fa-share-nodes"></i>
                </div>
            )
        } else {
            return (
                <div className="boutonsComm">
                    <i className="fa-regular fa-heart" onClick={this.liker}></i>
                    <i className="fa-regular fa-comment"></i>
                    <i className="fa-solid fa-share-nodes"></i>
                </div>
            )
        }
    }
}

export default BoutonsComm
