import React, {Component} from 'react'
import Stats from './Stats'
import ListeMessages from '../messages/ListeMessages'
import axios from 'axios'
import {ContextCombinedThemeAndId} from "../context/combined_context"
import ProvideCombinedContext from '../context/ProvideCombinedContext';


class BodyMurDeTweets extends Component{
    constructor(props) {
        super(props);
        this.state = {
            nbMessTotal : 0
        }
    }

    async recupereNbMess() {
        await axios.get("http://localhost:4000/apimessages/infos")
            .then((count) => {
                
            })
    }


    render(){
        let t = this.context;
        var n = (t.contextTheme.theme === 'light') ? "" : "V2"

        return (
        <div className={"corps"+n}>
            <Stats/>
            <ListeMessages changePage={this.props.changePage} />
        </div>
        )
    }
}
BodyMurDeTweets.contextType = ContextCombinedThemeAndId;

const WrappedBodyMurDeTweets = props => {
    return (
      <ProvideCombinedContext>
        <BodyMurDeTweets 
            changePage={props.changePage}  
        />
      </ProvideCombinedContext>
    );
};
export default WrappedBodyMurDeTweets;
