import React, { Component } from 'react'
import Login from './login/Login';
import MurDeTweets from './murDeTweets/MurDeTweets';
import SignIn from './signIn/SignIn'
import Utilisateur from './utilisateur/Utilisateur'

const ThemeContext = React.createContext(theme = false);

class NavigationPanel extends Component {
    constructor(props) {
        super(props)
        this.nuit = this.nuit.bind(this)
        this.isNuit = this.isNuit.bind(this)
        this.state = {
            nuit: false
        }
    }

    nuit() {
        this.setState({
            nuit: !this.state.nuit
        })
    }

    isNuit() {
        return (this.state.nuit)
    }


    render() {
        if (this.props.isConnected) {
            if (this.props.page === 'pageAccueil') {
                return (
                    <ThemeContext.Provider value={this.isNuit()}> 
                        <MurDeTweets changePage={this.props.changePage} setLogout={this.props.setLogout} nuit={this.nuit} />
                    </ThemeContext.Provider>
                )
            }
            if (this.props.page === 'utilisateur') {
                return (
                    <ThemeContext.Provider value={this.isNuit()}> 
                        <Utilisateur changePage={this.props.changePage} setLogout={this.props.setLogout} nuit={this.nuit} />
                    </ThemeContext.Provider>
                )
            }
        } else {
            if (this.props.page === 'connexion') {
                return (
                    <ThemeContext.Provider value={this.isNuit()}> 
                        <Login changePage={this.props.changePage} getConnected={this.props.getConnected} nuit={this.nuit} />
                    </ThemeContext.Provider>
                )
            }
            if (this.props.page === 'inscription') {
                return (
                    <ThemeContext.Provider value={this.isNuit()}> 
                        <SignIn changePage={this.props.changePage} getConnected={this.props.getConnected} nuit={this.nuit} />
                    </ThemeContext.Provider>
                )
            }
        }
        
    }
}

export default NavigationPanel;
