import React, { Component } from "react";
import BoutonsMsg from "./BoutonsMsg"
import {ContextCombinedThemeAndId} from "../context/combined_context"
import ProvideCombinedContext from '../context/ProvideCombinedContext';
import axios from "axios";


class Message extends Component{
    constructor(props){
        super(props)
        this.delMess = this.delMess.bind(this);
        this.utilisateur = this.utilisateur.bind(this);
        this.isModifMess = this.isModifMess.bind(this);
        this.modifMess1 = this.modifMess1.bind(this);
        // this.modifMess2 = this.modifMess2.bind(this);
        this.textAreaChange = this.textAreaChange.bind(this);
        this.state = {
            modifMsg:false,
            old_message:this.props.message,
            new_message: ""
        };
        console.log("message : " + this.state.old_message);
        console.log("message2 : " + this.props.message)
    }

    utilisateur() {
        this.props.changePage('utilisateur');
    }

    delMess() {
        this.props.delMess(this.props.idMessage, this.state.old_message);
    }

    modifMess1() {
        console.log("modifMess1()")
        this.setState({
            modifMsg: !this.state.modifMsg,
            old_message: this.state.old_message,
            new_message: this.state.old_message
        })
    }

    // async modifMess2() {
    //     console.log("modifMess2()")
    //     let t = this.context;
    //     const idUser = t.contextId.id;
    //     await axios.put(`/user/${idUser}/messages`, {new_message: this.state.new_message, old_message: this.state.old_message})
    //         .then((res) => {
    //             console.log("j'ai modifié le message !");
    //             this.setState({
    //                 modifMsg: !this.state.modifMsg,
    //                 old_message: this.state.new_message,
    //                 new_message: ""
    //             })
    //         })
    //         .catch(e => {console.log({error:e.response.data.message})})
    // }

    isModifMess() {
        return (this.state.modifMsg)
    }

    textAreaChange() {
        var contenu = document.getElementById("messagetxtarea").value;
        this.setState({
            modifMsg: this.state.modifMsg,
            old_message: this.state.old_message,
            new_message: contenu
        })
    }


    render(){
        let t = this.context;
        var n = (t.contextTheme.theme === 'light') ? "" : "V2"

        var pMessage = <p id={"message"+n}>{this.state.old_message}</p>
        var txtAreaMessage = <textarea id={"messagetxtarea"+n} name="messagetxtarea" onChange={this.textAreaChange} defaultValue={this.state.old_message}></textarea>
        var m = this.isModifMess() ? txtAreaMessage : pMessage

        return(
            <div className="msg">
                <div className={"box_msg"+n}>
                    <div id="dessusMsg">
                        <div>
                            <em onClick={this.utilisateur}>{this.props.pseudo} </em>
                            <button>+</button>
                        </div>  
                        <p><i>{this.props.date}</i></p>
                    </div>
                    {m}
                    <BoutonsMsg isUtilisateur = {this.props.isUtilisateur} delMess={this.delMess} modifMess1={this.modifMess1}  isModifMess={this.isModifMess} />
                </div>
                <br/>
            </div>
        )
    }
}
Message.contextType = ContextCombinedThemeAndId;

const WrappedMessage = props => {
    return (
      <ProvideCombinedContext>
        <Message changePage={props.changePage} />
      </ProvideCombinedContext>
    );
};
export default WrappedMessage;
