import React, { Component } from 'react'
import Login from './login/Login';
import MurDeTweets from './murDeTweets/MurDeTweets';
import SignIn from './signIn/SignIn'
import Utilisateur from './utilisateur/Utilisateur'

export const ThemeContext = React.createContext({
    theme : 'light',
    modifThem : (theme) => {
        if (theme == 'light') theme = 'dark';
        else {
            if (theme == 'dark') theme = 'light';
        }
    }
});

class NavigationPanel extends Component {
    constructor(props) {
        super(props)
        this.isNuit = this.isNuit.bind(this)
        this.modifTheme
        this.state = {
            theme: false
        }
    }

    isNuit() {
        return (this.state.nuit)
    }


    render() {
        if (this.props.isConnected) {
            if (this.props.page === 'pageAccueil') {
                return (
                    <ThemeContext.Provider value={this.state}> 
                        <MurDeTweets changePage={this.props.changePage} setLogout={this.props.setLogout} />
                    </ThemeContext.Provider>
                )
            }
            if (this.props.page === 'utilisateur') {
                return (
                    <ThemeContext.Provider value={this.isNuit()}> 
                        <Utilisateur changePage={this.props.changePage} setLogout={this.props.setLogout} />
                    </ThemeContext.Provider>
                )
            }
        } else {
            if (this.props.page === 'connexion') {
                return (
                    <ThemeContext.Provider value={this.isNuit()}> 
                        <Login changePage={this.props.changePage} getConnected={this.props.getConnected} />
                    </ThemeContext.Provider>
                )
            }
            if (this.props.page === 'inscription') {
                return (
                    <ThemeContext.Provider value={this.isNuit()}> 
                        <SignIn changePage={this.props.changePage} getConnected={this.props.getConnected} />
                    </ThemeContext.Provider>
                )
            }
        }
        
    }
}

export default NavigationPanel;
