import React, { Component } from 'react'
import Message from '../messages/Message'
import axios from 'axios'
import {IdContext} from '../context/id_context'

class UtilisateurMessage extends Component {
    constructor(props) {
        super(props);
        this.recupereMessages = this.recupereMessages.bind(this);
        // this.delMess = this.delMess.bind(this)
        this.state = {
            listeMess : []
        }
    }

    // delMess(indexMess) {
    //     console.log("coucou de delMess(param) utilisateur");
    //     console.log(indexMess);
    //     for (var i=0; i<this.state.liste.length; i++) {
    //         if (this.state.liste[i].idMessage === indexMess) {
    //             this.state.liste.splice(i,1);
    //         }
    //     }
    //     this.setState({
    //         liste: this.state.liste
    //     })
    // }

    async recupereMessages(nbAffichage) {
        // console.log("nbAffichage = " + nbAffichage);
        await axios.get(`http://localhost:4000/apimessages/messages/getNMessages/${nbAffichage}/fromUser/${this.props.profil}`)
            .then((res) => {
                this.setState({
                    listeMess : res.data
                })
            })
            .catch(e => {console.log({error:e.response.data.message})})
    }

    render() {
        if (this.state.listeMess.length === 0) this.recupereMessages(10);
        let t = this.context;

        return (
            <div>
                <h3>Liste de Messages</h3>
                <ul className="liste_messages">
                    {this.state.listeMess.map((mess, index) => (
                        <li key={index}>
                            <Message 
                                isUtilisateur={(mess.loginUser!==t.id)? false : true} 
                                // delMess={this.delMess}
                                idMessage={mess._id}
                                pseudo={mess.loginUser}
                                date={mess.date}
                                message={mess.message}
                            />
                        </li>
                    ))}
                </ul>
            </div>
        );
    }
}
UtilisateurMessage.contextType = IdContext;

export default UtilisateurMessage;
