import React, {Component} from "react";
import Message from './Message'
import NvMessage from './NvMessage'

class ListeMessages extends Component{
    constructor(props){
        super(props)
        this.ajoutMessage = this.ajoutMessage.bind(this)
        this.delComm = this.delComm.bind(this)
        this.state = {
            liste : [   
                {changePage:this.props.changePage, isUtilisateur:false, delComm:this.props.delComm, isNuit:this.props.isNuit, id:4, pseudo:"elMachoProtector", date:"27/02/2022", message: "Update de la dernière fois : on m'a parlé de Encanto: la fantastique famille Madrigal. Je déconseille !! Le film est pas mal mais les chansons sont encore plus entétantes que celle de la Reine des Neiges, dont je voulais me débarasser :'("},
                {changePage:this.props.changePage, isUtilisateur:false, delComm:this.props.delComm, isNuit:this.props.isNuit, id:3, pseudo:"elMachoProtector", date:"17/02/2022", message: "Quelqu'un aurait des films pour enfants à conseiller ?? J'en peux plus de revisionner la reine des neiges avec mes gosses !! Aidez-moi XDDD"},
                {changePage:this.props.changePage, isUtilisateur:false, delComm:this.props.delComm, isNuit:this.props.isNuit, id:2, pseudo:"darkSasukeVegeta", date:"07/02/2022", message: "NAN MAI WSSSHHH, ON SE RAIVOOOOOLTE ISSIIIIIII !!!! JPP LES GENS QUI RESPECTENT PAS LES PIGEONS VOUS ETES DES GROS MECHANT, WSH ILS ON UN COEUR AUSSI VOUS RESPECTER R"},
                {changePage:this.props.changePage, isUtilisateur:true, delComm:this.props.delComm, isNuit:this.props.isNuit, id:1, pseudo:"filanteetoile", date:"01/01/2022", message: "Bonne annee !!!! Pleins de bonnes choses à tous, des bisous et plein de bonne humeur !!!"},
                {changePage:this.props.changePage, isUtilisateur:true, delComm:this.props.delComm, isNuit:this.props.isNuit, id:0, pseudo:"filanteetoile", date:"01/01/2022", message: "Bonne annee !!!! Pleins de bonnes choses à tous, des bisous et plein de bonne humeur !!!"}
            ]
        }
    }

    ajoutMessage(msg) {
        this.state.liste.unshift({ 
            changePage:this.props.changePage, isUtilisateur:true, delComm:this.props.delComm, isNuit:this.props.isNuit, id:0, pseudo:"filanteetoile",
            date:"17/02/2022",
            message:msg
        });
        this.setState({
            liste: this.state.liste
        })
    }

    delComm(indexMess) {
        for (var i=0; i<this.state.liste.length; i++) {
            console.log(i)
            if (this.state.liste[i].id == indexMess) {
                console.log("j'ai trouvé", this.state.liste[i].id)
                this.state.liste.splice(i,1);
            }
        }
        this.setState({
            liste: this.state.liste
        })
    }

    render() {
        return(
            <div className="messages">
                <NvMessage ajoutMessage={this.ajoutMessage} />
                <h3>Liste de Messages</h3>
                <ul className="liste_messages">
                    {this.state.liste.map((mess) => (
                    <li key={mess.id}>
                        <Message 
                            changePage={mess.changePage} 
                            isUtilisateur={mess.isUtilisateur} 
                            delComm={mess.delComm}
                            isNuit={mess.isNuit}
                            id
                            pseudo={mess.pseudo}
                            date={mess.date}
                            message={mess.message}/>
                    </li>
                    ))}
                </ul>
            </div>
        )
    }
}

export default ListeMessages
