import React, { Component } from "react";
import BoutonsMsg from "./BoutonsMsg"

class Message extends Component{
    constructor(props){
        super(props)
        this.delComm = this.delComm.bind(this)
        this.utilisateur = this.utilisateur.bind(this)
    }

    utilisateur() {
        this.props.changePage('utilisateur')
    }

    delComm() {
        this.props.delComm(this.props.idMessage);
    }

    render(){
        var n = this.props.isNuit() ? "V2" : ""
        return(
            <div className="msg">
                <div className={"box_msg"+n}>
                    <div id="dessusMsg">
                        <div>
                            <em onClick={this.utilisateur}>{this.props.pseudo} </em>
                            <button>+</button>
                        </div>  
                        <p><i>{this.props.date}</i></p>
                    </div>
                    <p id={"message"}>{this.props.message}</p>
                    <BoutonsMsg isUtilisateur = {this.props.isUtilisateur} delComm={this.delComm}/>
                </div>
                <br/>
            </div>
        )
    }
}

export default Message
