import React, { Component } from "react";
import BoutonsMsg from "./BoutonsMsg"

class Message extends Component{
    constructor(props){
        super(props)
        this.delMess = this.delMess.bind(this)
        this.utilisateur = this.utilisateur.bind(this)
        this.state = {
            
        }
    }

    utilisateur() {
        this.props.changePage('utilisateur')
    }

    delMess() {
        console.log("coucou de delMess()")
        console.log(this.props.idMessage)
        this.props.delMess(this.props.idMessage);
    }

    modifMess() {

    }

    render(){
        var n = this.props.isNuit() ? "V2" : ""
        return(
            <div className="msg">
                <div className={"box_msg"+n}>
                    <div id="dessusMsg">
                        <div>
                            <em onClick={this.utilisateur}>{this.props.pseudo} </em>
                            <button>+</button>
                        </div>  
                        <p><i>{this.props.date}</i></p>
                    </div>
                    <p id={"message"+n}>{this.props.message}</p>
                    <BoutonsMsg isUtilisateur = {this.props.isUtilisateur} delMess={this.delMess} isNuit={this.props.isNuit}/>
                </div>
                <br/>
            </div>
        )
    }
}

export default Message
