import React, {Component} from "react";
import Message from './Message'
import NvMessage from './NvMessage'

class ListeMessages extends Component{
    constructor(props){
        super(props)
        this.ajoutMessage = this.ajoutMessage.bind(this)
        this.state = {
            liste: [<Message changePage={this.props.changePage} pseudo="elMachoProtector" date="27/02/2022"
                message="Update de la dernière fois : on m'a parlé de Encanto: la fantastique famille Madrigal. Je déconseille !! Le film est pas mal mais les chansons sont encore plus entétantes que celle de la Reine des Neiges, dont je voulais me débarasser :'(" />

                , <Message changePage={this.props.changePage} pseudo="elMachoProtector" date="17/02/2022"
                    message="Quelqu'un aurait des films pour enfants à conseiller ?? J'en peux plus de revisionner la reine des neiges avec mes gosses !! Aidez-moi XDDD" />

                , <Message changePage={this.props.changePage} pseudo="darkSasukeVegeta" date="07/02/2022"
                    message="NAN MAI WSSSHHH, ON SE RAIVOOOOOLTE ISSIIIIIII !!!! JPP LES GENS QUI RESPECTENT PAS LES PIGEONS VOUS ETES DES GROS MECHANT, WSH ILS ON UN COEUR AUSSI VOUS RESPECTER R" />

                , <Message changePage={this.props.changePage} isUtilisateur = {true}pseudo="filanteetoile" date="01/01/2022"
                    message="Bonne annee !!!! Pleins de bonnes choses à tous, des bisous et plein de bonne humeur !!!" />

                , <Message changePage={this.props.changePage} isUtilisateur = {true}pseudo="filanteetoile" date="01/01/2022"
                    message="Bonne annee !!!! Pleins de bonnes choses à tous, des bisous et plein de bonne humeur !!!" />
            ]
        }
    }

    ajoutMessage(msg) {
        this.state.liste.unshift(<Message changePage={this.props.changePage} isUtilisateur = {true}pseudo="filanteetoile" date="17/02/2022"
            message={msg} />
        );
        this.setState({
            liste: this.state.liste
        })
    }

    render() {
        return(
            <div className="messages">
                <NvMessage ajoutMessage={this.ajoutMessage} />
                <h3>Liste de Messages</h3>
                <div className="liste_messages">
                    {this.state.liste}
                </div>
            </div>
        )
    }
}

export default ListeMessages
